pragma solidity ^0.8.26;

import { IAssemblyPassports } from "./interfaces/IAssemblyPassports.sol";
import { ERC1155 } from "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title AssemblyPassports
 * @notice Non-transferable ERC-1155 membership credentials for an Assembly
 * @dev Each token ID represents a different passport type (member, contributor, etc.)
 */
contract AssemblyPassports is IAssemblyPassports, ERC1155, Ownable, ReentrancyGuard {
    /// @notice Counter for passport type IDs
    uint256 private _nextTokenId;

    /// @notice Mapping from token ID to passport type config
    mapping(uint256 => PassportType) public passportTypes;

    /// @notice Allowlist: tokenId => address => allowed
    mapping(uint256 => mapping(address => bool)) public allowlist;

    /**
     * @notice Initializes the passport contract
     * @param assemblyAddress The Assembly contract that owns this passport system
     */
    constructor(address assemblyAddress) ERC1155("") Ownable(assemblyAddress) {
        _nextTokenId = 1; // Start token IDs at 1
    }

    // ============ Admin Functions ============

    /**
     * @notice Creates a new passport type
     * @param name Human-readable name for this passport type
     * @param metadataUri URI pointing to passport metadata (IPFS/URL)
     * @param isOpen Whether anyone can mint (true) or allowlist only (false)
     * @return tokenId The ID of the newly created passport type
     */
    function createPassportType(
        string calldata name,
        string calldata metadataUri,
        bool isOpen
    ) external onlyOwner returns (uint256 tokenId) {
        if (bytes(name).length == 0) revert EmptyName();
        if (bytes(metadataUri).length == 0) revert EmptyURI();

        tokenId = _nextTokenId++;

        passportTypes[tokenId] = PassportType({ name: name, uri: metadataUri, isOpen: isOpen, exists: true });

        emit PassportTypeCreated(tokenId, name, metadataUri, isOpen);
    }

    /**
     * @notice Adds addresses to the allowlist for a passport type
     * @param tokenId The passport type ID
     * @param accounts Array of addresses to allowlist
     */
    function addToAllowlist(uint256 tokenId, address[] calldata accounts) external onlyOwner {
        if (!passportTypes[tokenId].exists) revert PassportTypeDoesNotExist();
        if (accounts.length == 0) revert EmptyAddressArray();

        for (uint256 i = 0; i < accounts.length; i++) {
            allowlist[tokenId][accounts[i]] = true;
            emit AllowlistUpdated(tokenId, accounts[i], true);
        }
    }

    /**
     * @notice Updates the URI for a passport type
     * @param tokenId The passport type ID
     * @param newUri New metadata URI
     */
    function updatePassportURI(uint256 tokenId, string calldata newUri) external onlyOwner {
        if (!passportTypes[tokenId].exists) revert PassportTypeDoesNotExist();
        if (bytes(newUri).length == 0) revert EmptyURI();

        passportTypes[tokenId].uri = newUri;
    }

    // ============ Minting Functions ============

    /**
     * @notice Mints a passport to the caller
     * @param tokenId The passport type to mint
     */
    function mint(uint256 tokenId) external nonReentrant {
        PassportType memory passportType = passportTypes[tokenId];

        if (!passportType.exists) revert PassportTypeDoesNotExist();
        if (balanceOf(msg.sender, tokenId) > 0) revert AlreadyHoldsPassport();

        // Check access: must be open OR caller must be allowlisted
        if (!passportType.isOpen) {
            if (!allowlist[tokenId][msg.sender]) revert NotAllowlisted();
        }

        _mint(msg.sender, tokenId, 1, "");

        emit PassportMinted(msg.sender, tokenId);
    }

    /**
     * @notice Mints a passport to a specific address (admin only)
     * @param to Address to mint passport to
     * @param tokenId The passport type to mint
     */
    function mintTo(address to, uint256 tokenId) external onlyOwner nonReentrant {
        if (!passportTypes[tokenId].exists) revert PassportTypeDoesNotExist();
        if (balanceOf(to, tokenId) > 0) revert AlreadyHoldsPassport();

        _mint(to, tokenId, 1, "");

        emit PassportMinted(to, tokenId);
    }

    // ============ View Functions ============

    /**
     * @notice Returns the URI for a specific passport type
     * @param tokenId The passport type ID
     */
    function uri(uint256 tokenId) public view override(ERC1155, IAssemblyPassports) returns (string memory) {
        if (!passportTypes[tokenId].exists) revert PassportTypeDoesNotExist();
        return passportTypes[tokenId].uri;
    }

    /**
     * @notice Returns the balance of passports held by an account for a specific type
     * @param account Address to query
     * @param tokenId Passport type ID
     */
    function balanceOf(
        address account,
        uint256 tokenId
    ) public view override(ERC1155, IAssemblyPassports) returns (uint256) {
        return super.balanceOf(account, tokenId);
    }

    /**
     * @notice Checks if an address holds a specific passport
     * @param account Address to check
     * @param tokenId Passport type ID
     */
    function holdsPassport(address account, uint256 tokenId) external view returns (bool) {
        return balanceOf(account, tokenId) > 0;
    }

    /**
     * @notice Checks if an address holds any of the specified passports
     * @param account Address to check
     * @param tokenIds Array of passport type IDs
     */
    function holdsAnyPassport(address account, uint256[] calldata tokenIds) external view returns (bool) {
        for (uint256 i = 0; i < tokenIds.length; i++) {
            if (balanceOf(account, tokenIds[i]) > 0) {
                return true;
            }
        }
        return false;
    }

    /**
     * @notice Gets the next token ID that will be assigned
     */
    function nextTokenId() external view returns (uint256) {
        return _nextTokenId;
    }

    // ============ Transfer Restrictions ============

    /**
     * @notice Overrides to make passports non-transferable (soulbound)
     */
    function safeTransferFrom(address, address, uint256, uint256, bytes memory) public pure override {
        revert TransfersNotAllowed();
    }

    /**
     * @notice Overrides to make passports non-transferable (soulbound)
     */
    function safeBatchTransferFrom(
        address,
        address,
        uint256[] memory,
        uint256[] memory,
        bytes memory
    ) public pure override {
        revert TransfersNotAllowed();
    }
}
","keccak256":"0x54c78db1f7040490f802ed8cbff6f2ab03950d29e2d1d17cd2769f7c5f90ae6f","license":"MIT"},"contracts/Contest.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import { IContest } from "./interfaces/IContest.sol";
import { AssemblyPassports } from "./AssemblyPassports.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title Contest
 * @notice A time-bound voting mechanism for Assemblies to make decisions
 * @dev Designed to be cloned - uses initialize pattern instead of constructor
 */
contract Contest is IContest, ReentrancyGuard {
    // ============ State Variables ============

    /// @notice Maximum number of options allowed per contest
    uint256 public constant MAX_OPTIONS = 10;

    /// @notice Whether this contract has been initialized
    bool private _initialized;

    /// @notice The Assembly that created this contest
    address public assemblyAddress;

    /// @notice The passport contract for gating
    address public passports;

    /// @notice Contest prompt/question
    string public prompt;

    /// @notice Array of voting options
    string[] public options;

    /// @notice Timestamp when voting ends
    uint256 public votingEnd;

    /// @notice Required passport token IDs (empty array = open voting)
    uint256[] public requiredPassports;

    /// @notice Vote tallies: optionIndex => vote count
    mapping(uint256 => uint256) public voteTally;

    /// @notice Track who has voted: address => hasVoted
    mapping(address => bool) public hasVoted;

    /// @notice Track individual votes: address => optionIndex
    mapping(address => uint256) public userVote;

    /// @notice Total number of votes cast
    uint256 public totalVotes;

    // ============ Modifiers ============

    modifier onlyAssembly() {
        if (msg.sender != assemblyAddress) revert OnlyAssembly();
        _;
    }

    modifier votingActive() {
        if (block.timestamp >= votingEnd) revert VotingEnded();
        _;
    }

    modifier votingComplete() {
        if (block.timestamp < votingEnd) revert VotingNotEnded();
        _;
    }

    // ============ Initialization ============

    /**
     * @notice Initializes the contest (called by Assembly after cloning)
     * @param _assembly Address of the Assembly creating this contest
     * @param _passports Address of the passport contract
     * @param _prompt The question or prompt for this contest
     * @param _options Array of voting options
     * @param _votingDuration Duration of voting period in seconds
     * @param _requiredPassports Array of passport token IDs required to vote
     */
    function __init(
        address _assembly,
        address _passports,
        string memory _prompt,
        string[] memory _options,
        uint256 _votingDuration,
        uint256[] memory _requiredPassports
    ) external {
        if (_initialized) revert AlreadyInitialized();

        if (bytes(_prompt).length == 0) revert EmptyPrompt();
        if (_options.length < 2) revert NotEnoughOptions();
        if (_options.length > MAX_OPTIONS) revert TooManyOptions();
        if (_votingDuration == 0) revert InvalidVotingPeriod();

        // Validate all options are non-empty
        for (uint256 i = 0; i < _options.length; i++) {
            if (bytes(_options[i]).length == 0) revert EmptyOption();
        }

        assemblyAddress = _assembly;
        passports = _passports;
        prompt = _prompt;
        options = _options;
        votingEnd = block.timestamp + _votingDuration;
        requiredPassports = _requiredPassports;
        _initialized = true;

        emit ContestCreated(_assembly, _prompt, votingEnd, _options.length, _requiredPassports.length > 0);
    }

    // ============ Voting Functions ============

    /**
     * @notice Cast a vote for an option
     * @param optionIndex Index of the option to vote for
     */
    function vote(uint256 optionIndex) external nonReentrant votingActive {
        if (!_initialized) revert NotInitialized();
        if (hasVoted[msg.sender]) revert AlreadyVoted();
        if (optionIndex >= options.length) revert InvalidOption();

        // Check passport requirements if gated
        if (requiredPassports.length > 0) {
            if (!AssemblyPassports(passports).holdsAnyPassport(msg.sender, requiredPassports)) {
                revert MissingRequiredPassport();
            }
        }

        // Record the vote
        hasVoted[msg.sender] = true;
        userVote[msg.sender] = optionIndex;
        voteTally[optionIndex]++;
        totalVotes++;

        emit VoteCast(msg.sender, optionIndex);
    }

    // ============ View Functions ============

    /**
     * @notice Get all voting options
     */
    function getOptions() external view returns (string[] memory) {
        return options;
    }

    /**
     * @notice Get all required passport IDs
     */
    function getRequiredPassports() external view returns (uint256[] memory) {
        return requiredPassports;
    }

    /**
     * @notice Get complete vote results
     * @return optionNames Array of option strings
     * @return votes Array of vote counts for each option
     * @return total Total votes cast
     */
    function getResults() external view returns (string[] memory optionNames, uint256[] memory votes, uint256 total) {
        optionNames = options;
        votes = new uint256[](options.length);

        for (uint256 i = 0; i < options.length; i++) {
            votes[i] = voteTally[i];
        }

        total = totalVotes;
    }

    /**
     * @notice Get the winning option index
     * @dev In case of tie, returns the lowest index option
     * @return winningIndex Index of the winning option
     * @return winningVotes Number of votes for the winner
     */
    function getWinner() external view votingComplete returns (uint256 winningIndex, uint256 winningVotes) {
        winningIndex = 0;
        winningVotes = voteTally[0];

        for (uint256 i = 1; i < options.length; i++) {
            if (voteTally[i] > winningVotes) {
                winningIndex = i;
                winningVotes = voteTally[i];
            }
        }
    }

    /**
     * @notice Check if voting is currently active
     */
    function isActive() external view returns (bool) {
        return _initialized && block.timestamp < votingEnd;
    }

    /**
     * @notice Check if a specific address can vote
     * @param voter Address to check
     */
    function canVote(address voter) external view returns (bool) {
        if (!_initialized) return false;

        // Check if voting is active
        if (block.timestamp >= votingEnd) return false;

        // Check if already voted
        if (hasVoted[voter]) return false;

        // Check passport requirements
        if (requiredPassports.length > 0) {
            return AssemblyPassports(passports).holdsAnyPassport(voter, requiredPassports);
        }

        return true;
    }

    /**
     * @notice Get time remaining in voting period
     * @return seconds remaining (0 if ended)
     */
    function timeRemaining() external view returns (uint256) {
        if (!_initialized || block.timestamp >= votingEnd) return 0;
        return votingEnd - block.timestamp;
    }

    /**
     * @notice Check if contest is gated by passports
     */
    function isGated() external view returns (bool) {
        return requiredPassports.length > 0;
    }

    /**
     * @notice Check if contest has been initialized
     */
    function initialized() external view returns (bool) {
        return _initialized;
    }
}
","keccak256":"0x8100801d1a34788c7899e4002a8f940cb3f1e5ef2046b067c3bfb86867a0ffed","license":"MIT"},"contracts/interfaces/IAssemblyPassports.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IAssemblyPassports
 * @notice Interface for Assembly passport contracts
 */
interface IAssemblyPassports {
    // ============ Structs ============

    struct PassportType {
        string name;
        string uri;
        bool isOpen;
        bool exists;
    }

    // ============ Events ============

    event PassportTypeCreated(uint256 indexed tokenId, string name, string uri, bool isOpen);

    event PassportMinted(address indexed holder, uint256 indexed tokenId);

    event AllowlistUpdated(uint256 indexed tokenId, address indexed account, bool allowed);

    // ============ Errors ============

    error TransfersNotAllowed();
    error PassportTypeDoesNotExist();
    error AlreadyHoldsPassport();
    error NotAllowlisted();
    error PassportTypeNotOpen();
    error EmptyName();
    error EmptyURI();
    error EmptyAddressArray();

    // ============ Admin Functions ============

    function createPassportType(
        string calldata name,
        string calldata metadataUri,
        bool isOpen
    ) external returns (uint256 tokenId);

    function addToAllowlist(uint256 tokenId, address[] calldata accounts) external;

    function updatePassportURI(uint256 tokenId, string calldata newUri) external;

    // ============ Minting Functions ============

    function mint(uint256 tokenId) external;

    function mintTo(address to, uint256 tokenId) external;

    // ============ View Functions ============

    function passportTypes(
        uint256 tokenId
    ) external view returns (string memory name, string memory uri, bool isOpen, bool exists);

    function allowlist(uint256 tokenId, address account) external view returns (bool);

    function holdsPassport(address account, uint256 tokenId) external view returns (bool);

    function holdsAnyPassport(address account, uint256[] calldata tokenIds) external view returns (bool);

    function nextTokenId() external view returns (uint256);

    function uri(uint256 tokenId) external view returns (string memory);

    function balanceOf(address account, uint256 id) external view returns (uint256);
}
","keccak256":"0x29ba33ba6620c5b2823f45aeb2dc1f00886b27237d3fde4c3054a4515d05064b","license":"MIT"},"contracts/interfaces/IContest.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { IAssemblyPassports } from "./IAssemblyPassports.sol";

/**
 * @title IContest
 * @notice Interface for Contest contracts
 */
interface IContest {
    // ============ Events ============

    event ContestCreated(
        address indexed assemblyAddress,
        string prompt,
        uint256 votingEnd,
        uint256 optionsCount,
        bool isGated
    );

    event VoteCast(address indexed voter, uint256 indexed optionIndex);

    // ============ Errors ============

    error AlreadyInitialized();
    error NotInitialized();
    error OnlyAssembly();
    error TooManyOptions();
    error NotEnoughOptions();
    error VotingEnded();
    error VotingNotEnded();
    error AlreadyVoted();
    error InvalidOption();
    error InvalidVotingPeriod();
    error MissingRequiredPassport();
    error EmptyPrompt();
    error EmptyOption();

    // ============ Initialization ============

    function __init(
        address _assembly,
        address _passports,
        string memory _prompt,
        string[] memory _options,
        uint256 _votingDuration,
        uint256[] memory _requiredPassports
    ) external;

    // ============ Voting Functions ============

    function vote(uint256 optionIndex) external;

    // ============ View Functions ============

    function MAX_OPTIONS() external view returns (uint256);

    function assemblyAddress() external view returns (address);

    function passports() external view returns (address);

    function prompt() external view returns (string memory);

    function options(uint256 index) external view returns (string memory);

    function votingEnd() external view returns (uint256);

    function requiredPassports(uint256 index) external view returns (uint256);

    function voteTally(uint256 optionIndex) external view returns (uint256);

    function hasVoted(address voter) external view returns (bool);

    function userVote(address voter) external view returns (uint256);

    function totalVotes() external view returns (uint256);

    function getOptions() external view returns (string[] memory);

    function getRequiredPassports() external view returns (uint256[] memory);

    function getResults() external view returns (string[] memory optionNames, uint256[] memory votes, uint256 total);

    function getWinner() external view returns (uint256 winningIndex, uint256 winningVotes);

    function isActive() external view returns (bool);

    function canVote(address voter) external view returns (bool);

    function timeRemaining() external view returns (uint256);

    function isGated() external view returns (bool);

    function initialized() external view returns (bool);
}
