contracts/AgoraFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import { IAgoraFactory } from "./interfaces/IAgoraFactory.sol";
import { Assembly } from "./Assembly.sol";
import { Contest } from "./Contest.sol";
import { Clones } from "@openzeppelin/contracts/proxy/Clones.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title AgoraFactory
 * @notice Factory contract for deploying new Assemblies
 * @dev Uses minimal clone pattern for gas-efficient Assembly deployment
 */
contract AgoraFactory is IAgoraFactory, Ownable {
    /// @notice Implementation contract for Assembly clones
    address public immutable assemblyImplementation;

    /// @notice Implementation contract for Contest clones
    address public immutable contestImplementation;

    /// @notice Array of all deployed Assemblies
    address[] public assemblies;

    /// @notice Mapping to verify if address is a valid Assembly from this factory
    mapping(address => bool) public isAssembly;

    /// @notice Mapping from creator to their Assemblies
    mapping(address => address[]) public assembliesByCreator;

    /**
     * @notice Deploys the factory and implementation contracts
     * @param factoryOwner Address that will own the factory
     */
    constructor(address factoryOwner) Ownable(factoryOwner) {
        // Deploy Contest implementation first (needed by Assembly)
        contestImplementation = address(new Contest());

        // Deploy Assembly implementation with contest implementation address
        // Note: we pass address(this) as creator for the implementation
        // This is just for deployment; actual Assemblies will have proper creators
        assemblyImplementation = address(new Assembly(address(this), "ipfs://implementation", contestImplementation));

        if (assemblyImplementation == address(0)) revert InvalidImplementation();
        if (contestImplementation == address(0)) revert InvalidImplementation();

        emit FactoryDeployed(address(this), assemblyImplementation, contestImplementation);
    }

    // ============ Assembly Creation ============

    /**
     * @notice Creates a new Assembly
     * @param metadataURI URI pointing to Assembly metadata (IPFS/URL)
     * @return assemblyAddress Address of the newly created Assembly
     */
    function createAssembly(string calldata metadataURI) external returns (address assemblyAddress) {
        if (bytes(metadataURI).length == 0) revert EmptyMetadataURI();

        // Clone the Assembly implementation
        assemblyAddress = Clones.clone(assemblyImplementation);

        // Initialize the Assembly with the creator as admin
        Assembly(assemblyAddress).__init(msg.sender, metadataURI, contestImplementation);

        // Track the Assembly
        uint256 assemblyIndex = assemblies.length;
        assemblies.push(assemblyAddress);
        isAssembly[assemblyAddress] = true;
        assembliesByCreator[msg.sender].push(assemblyAddress);

        emit AssemblyCreated(assemblyAddress, msg.sender, metadataURI, assemblyIndex);
    }

    // ============ View Functions ============

    /**
     * @notice Get total number of Assemblies
     */
    function getAssemblyCount() external view returns (uint256) {
        return assemblies.length;
    }

    /**
     * @notice Get Assembly address at specific index
     * @param index Index in the assemblies array
     */
    function getAssembly(uint256 index) external view returns (address) {
        if (index >= assemblies.length) revert AssemblyNotFound();
        return assemblies[index];
    }

    /**
     * @notice Get all Assembly addresses
     */
    function getAllAssemblies() external view returns (address[] memory) {
        return assemblies;
    }

    /**
     * @notice Get Assemblies created by a specific address
     * @param creator Address to query
     */
    function getAssembliesByCreator(address creator) external view returns (address[] memory) {
        return assembliesByCreator[creator];
    }

    /**
     * @notice Get paginated list of Assemblies
     * @param offset Starting index
     * @param limit Maximum number of results
     * @return paginatedAssemblies Array of Assembly addresses
     * @return total Total number of Assemblies
     */
    function getAssembliesPaginated(
        uint256 offset,
        uint256 limit
    ) external view returns (address[] memory paginatedAssemblies, uint256 total) {
        total = assemblies.length;

        if (offset >= total) {
            return (new address[](0), total);
        }

        uint256 end = offset + limit;
        if (end > total) {
            end = total;
        }

        uint256 resultLength = end - offset;
        paginatedAssemblies = new address[](resultLength);

        for (uint256 i = 0; i < resultLength; i++) {
            paginatedAssemblies[i] = assemblies[offset + i];
        }
    }

    /**
     * @notice Get the most recently created Assemblies
     * @param count Number of recent Assemblies to return
     */
    function getRecentAssemblies(uint256 count) external view returns (address[] memory recentAssemblies) {
        uint256 total = assemblies.length;

        if (count > total) {
            count = total;
        }

        recentAssemblies = new address[](count);

        for (uint256 i = 0; i < count; i++) {
            recentAssemblies[i] = assemblies[total - 1 - i];
        }
    }

    /**
     * @notice Verify if an address is a valid Assembly from this factory
     * @param potentialAssembly Address to verify
     */
    function verifyAssembly(address potentialAssembly) external view returns (bool) {
        return isAssembly[potentialAssembly];
    }

    /**
     * @notice Get factory information
     * @return factory Factory address
     * @return assemblyImpl Assembly implementation address
     * @return contestImpl Contest implementation address
     * @return totalAssemblies Total number of Assemblies created
     */
    function getFactoryInfo()
        external
        view
        returns (address factory, address assemblyImpl, address contestImpl, uint256 totalAssemblies)
    {
        factory = address(this);
        assemblyImpl = assemblyImplementation;
        contestImpl = contestImplementation;
        totalAssemblies = assemblies.length;
    }
}

contracts/Assembly.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import { IAssembly } from "./interfaces/IAssembly.sol";
import { AssemblyPassports } from "./AssemblyPassports.sol";
import { Contest } from "./Contest.sol";
import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { Clones } from "@openzeppelin/contracts/proxy/Clones.sol";

/**
 * @title Assembly
 * @notice Core governance unit that manages admins, passports, and contests
 * @dev Designed to be cloned - uses initialize pattern
 */
contract Assembly is IAssembly, AccessControl, ReentrancyGuard {
    // ============ State Variables ============

    /// @notice Admin role identifier
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    /// @notice Whether this Assembly has been initialized
    bool private _initialized;

    /// @notice Contest implementation for cloning
    address public contestImplementation;

    /// @notice The passport contract for this Assembly
    address public passports;

    /// @notice Assembly metadata URI (IPFS/URL)
    string public metadataURI;

    /// @notice Array of all contests created by this Assembly
    address[] public contests;

    /// @notice Mapping to check if address is a contest from this Assembly
    mapping(address => bool) public isContest;

    /// @notice Array to track all admin addresses
    address[] private _adminList;

    /// @notice Mapping to track if address is in admin list
    mapping(address => bool) private _isAdmin;

    /**
     * @notice Constructor for implementation contract
     * @dev This is only called once when deploying the implementation
     */
    constructor(address initialOwner, string memory _metadataURI, address _contestImplementation) {
        // For the implementation contract, we do minimal setup
        // The actual instances will be initialized via __init
        contestImplementation = _contestImplementation;
        metadataURI = _metadataURI;

        // Grant roles to prevent the implementation from being used directly
        _grantRole(DEFAULT_ADMIN_ROLE, initialOwner);
        _grantRole(ADMIN_ROLE, initialOwner);

        // Track admin in list
        _adminList.push(initialOwner);
        _isAdmin[initialOwner] = true;

        _initialized = true; // Mark implementation as initialized
    }

    // ============ Initialization ============

    /**
     * @notice Initializes a cloned Assembly
     * @param creator Initial admin of the Assembly
     * @param _metadataURI URI pointing to Assembly metadata
     * @param _contestImplementation Address of Contest implementation
     */
    function __init(address creator, string memory _metadataURI, address _contestImplementation) external {
        if (_initialized) revert AlreadyInitialized();
        if (bytes(_metadataURI).length == 0) revert EmptyMetadataURI();
        if (_contestImplementation == address(0)) revert InvalidContestImplementation();

        // Setup roles
        _grantRole(DEFAULT_ADMIN_ROLE, creator);
        _grantRole(ADMIN_ROLE, creator);

        // Track admin in list
        _adminList.push(creator);
        _isAdmin[creator] = true;

        // Deploy passport contract (this Assembly is the owner)
        passports = address(new AssemblyPassports(address(this)));

        // Store metadata and contest implementation
        metadataURI = _metadataURI;
        contestImplementation = _contestImplementation;
        _initialized = true;

        emit AssemblyInitialized(address(this), creator, _metadataURI);
    }

    // ============ Modifiers ============

    modifier whenInitialized() {
        if (!_initialized) revert NotInitialized();
        _;
    }

    // ============ Admin Management ============

    /**
     * @notice Add a new admin to the Assembly
     * @param newAdmin Address to grant admin role
     */
    function addAdmin(address newAdmin) external onlyRole(ADMIN_ROLE) whenInitialized {
        if (!_isAdmin[newAdmin]) {
            _adminList.push(newAdmin);
            _isAdmin[newAdmin] = true;
        }
        grantRole(ADMIN_ROLE, newAdmin);
        emit AdminAdded(newAdmin);
    }

    /**
     * @notice Remove an admin from the Assembly
     * @param admin Address to revoke admin role
     */
    function removeAdmin(address admin) external onlyRole(ADMIN_ROLE) whenInitialized {
        revokeRole(ADMIN_ROLE, admin);
        emit AdminRemoved(admin);
    }

    /**
     * @notice Check if an address is an admin
     * @param account Address to check
     */
    function isAdmin(address account) external view returns (bool) {
        return hasRole(ADMIN_ROLE, account);
    }

    /**
     * @notice Get total number of admins
     */
    function getAdminCount() external view returns (uint256) {
        return _adminList.length;
    }

    /**
     * @notice Get admin address at specific index
     * @param index Index in the admin set
     */
    function getAdminAt(uint256 index) external view returns (address) {
        require(index < _adminList.length, "Index out of bounds");
        return _adminList[index];
    }

    // ============ Metadata Management ============

    /**
     * @notice Update Assembly metadata URI
     * @param newURI New metadata URI
     */
    function updateMetadata(string calldata newURI) external onlyRole(ADMIN_ROLE) whenInitialized {
        if (bytes(newURI).length == 0) revert EmptyMetadataURI();

        string memory oldURI = metadataURI;
        metadataURI = newURI;

        emit MetadataUpdated(oldURI, newURI);
    }

    // ============ Passport Management ============

    /**
     * @notice Create a new passport type
     * @param name Name of the passport type
     * @param metadataUri URI for passport metadata
     * @param isOpen Whether anyone can mint (true) or allowlist only (false)
     * @return tokenId The newly created passport type ID
     */
    function createPassportType(
        string calldata name,
        string calldata metadataUri,
        bool isOpen
    ) external onlyRole(ADMIN_ROLE) whenInitialized returns (uint256 tokenId) {
        tokenId = AssemblyPassports(passports).createPassportType(name, metadataUri, isOpen);
        emit PassportTypeCreated(tokenId, name, isOpen);
    }

    /**
     * @notice Add addresses to passport allowlist
     * @param tokenId Passport type ID
     * @param accounts Addresses to allowlist
     */
    function addToPassportAllowlist(
        uint256 tokenId,
        address[] calldata accounts
    ) external onlyRole(ADMIN_ROLE) whenInitialized {
        AssemblyPassports(passports).addToAllowlist(tokenId, accounts);
    }

    /**
     * @notice Update passport type URI
     * @param tokenId Passport type ID
     * @param newUri New metadata URI
     */
    function updatePassportURI(uint256 tokenId, string calldata newUri) external onlyRole(ADMIN_ROLE) whenInitialized {
        AssemblyPassports(passports).updatePassportURI(tokenId, newUri);
    }

    /**
     * @notice Mint a passport to a specific address
     * @param to Address to receive passport
     * @param tokenId Passport type ID
     */
    function mintPassportTo(address to, uint256 tokenId) external onlyRole(ADMIN_ROLE) whenInitialized {
        AssemblyPassports(passports).mintTo(to, tokenId);
    }

    // ============ Contest Management ============

    /**
     * @notice Create a new contest
     * @param prompt The question or decision prompt
     * @param options Array of voting options
     * @param votingDuration Duration of voting period in seconds
     * @param requiredPassports Passport IDs required to vote (empty for open)
     * @return contest Address of the newly created contest
     */
    function createContest(
        string memory prompt,
        string[] memory options,
        uint256 votingDuration,
        uint256[] memory requiredPassports
    ) external onlyRole(ADMIN_ROLE) whenInitialized nonReentrant returns (address contest) {
        // Clone the contest implementation
        contest = Clones.clone(contestImplementation);

        // Initialize the contest
        Contest(contest).__init(address(this), address(passports), prompt, options, votingDuration, requiredPassports);

        // Track the contest
        contests.push(contest);
        isContest[contest] = true;

        emit ContestCreated(contest, prompt, block.timestamp + votingDuration);
    }

    /**
     * @notice Get total number of contests
     */
    function getContestCount() external view returns (uint256) {
        return contests.length;
    }

    /**
     * @notice Get contest address at index
     * @param index Index in contests array
     */
    function getContest(uint256 index) external view returns (address) {
        if (index >= contests.length) revert ContestNotFound();
        return contests[index];
    }

    /**
     * @notice Get all contest addresses
     */
    function getAllContests() external view returns (address[] memory) {
        return contests;
    }

    /**
     * @notice Get active contests
     * @return activeContests Array of currently active contest addresses
     */
    function getActiveContests() external view returns (address[] memory activeContests) {
        uint256 activeCount = 0;

        // First pass: count active contests
        for (uint256 i = 0; i < contests.length; i++) {
            if (Contest(contests[i]).isActive()) {
                activeCount++;
            }
        }

        // Second pass: populate array
        activeContests = new address[](activeCount);
        uint256 currentIndex = 0;

        for (uint256 i = 0; i < contests.length; i++) {
            if (Contest(contests[i]).isActive()) {
                activeContests[currentIndex] = contests[i];
                currentIndex++;
            }
        }
    }

    // ============ View Functions ============

    /**
     * @notice Get comprehensive Assembly information
     * @return passportContract Address of the passport contract
     * @return metadata Assembly metadata URI
     * @return contestCount Total number of contests
     * @return adminCount Total number of admins
     */
    function getInfo()
        external
        view
        returns (address passportContract, string memory metadata, uint256 contestCount, uint256 adminCount)
    {
        passportContract = address(passports);
        metadata = metadataURI;
        contestCount = contests.length;
        adminCount = _adminList.length;
    }

    /**
     * @notice Check if Assembly has been initialized
     */
    function initialized() external view returns (bool) {
        return _initialized;
    }
}

contracts/AssemblyPassports.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import { IAssemblyPassports } from "./interfaces/IAssemblyPassports.sol";
import { ERC1155 } from "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title AssemblyPassports
 * @notice Non-transferable ERC-1155 membership credentials for an Assembly
 * @dev Each token ID represents a different passport type (member, contributor, etc.)
 */
contract AssemblyPassports is IAssemblyPassports, ERC1155, Ownable, ReentrancyGuard {
    /// @notice Counter for passport type IDs
    uint256 private _nextTokenId;

    /// @notice Mapping from token ID to passport type config
    mapping(uint256 => PassportType) public passportTypes;

    /// @notice Allowlist: tokenId => address => allowed
    mapping(uint256 => mapping(address => bool)) public allowlist;

    /**
     * @notice Initializes the passport contract
     * @param assemblyAddress The Assembly contract that owns this passport system
     */
    constructor(address assemblyAddress) ERC1155("") Ownable(assemblyAddress) {
        _nextTokenId = 1; // Start token IDs at 1
    }

    // ============ Admin Functions ============

    /**
     * @notice Creates a new passport type
     * @param name Human-readable name for this passport type
     * @param metadataUri URI pointing to passport metadata (IPFS/URL)
     * @param isOpen Whether anyone can mint (true) or allowlist only (false)
     * @return tokenId The ID of the newly created passport type
     */
    function createPassportType(
        string calldata name,
        string calldata metadataUri,
        bool isOpen
    ) external onlyOwner returns (uint256 tokenId) {
        if (bytes(name).length == 0) revert EmptyName();
        if (bytes(metadataUri).length == 0) revert EmptyURI();

        tokenId = _nextTokenId++;

        passportTypes[tokenId] = PassportType({ name: name, uri: metadataUri, isOpen: isOpen, exists: true });

        emit PassportTypeCreated(tokenId, name, metadataUri, isOpen);
    }

    /**
     * @notice Adds addresses to the allowlist for a passport type
     * @param tokenId The passport type ID
     * @param accounts Array of addresses to allowlist
     */
    function addToAllowlist(uint256 tokenId, address[] calldata accounts) external onlyOwner {
        if (!passportTypes[tokenId].exists) revert PassportTypeDoesNotExist();
        if (accounts.length == 0) revert EmptyAddressArray();

        for (uint256 i = 0; i < accounts.length; i++) {
            allowlist[tokenId][accounts[i]] = true;
            emit AllowlistUpdated(tokenId, accounts[i], true);
        }
    }

    /**
     * @notice Updates the URI for a passport type
     * @param tokenId The passport type ID
     * @param newUri New metadata URI
     */
    function updatePassportURI(uint256 tokenId, string calldata newUri) external onlyOwner {
        if (!passportTypes[tokenId].exists) revert PassportTypeDoesNotExist();
        if (bytes(newUri).length == 0) revert EmptyURI();

        passportTypes[tokenId].uri = newUri;
    }

    // ============ Minting Functions ============

    /**
     * @notice Mints a passport to the caller
     * @param tokenId The passport type to mint
     */
    function mint(uint256 tokenId) external nonReentrant {
        PassportType memory passportType = passportTypes[tokenId];

        if (!passportType.exists) revert PassportTypeDoesNotExist();
        if (balanceOf(msg.sender, tokenId) > 0) revert AlreadyHoldsPassport();

        // Check access: must be open OR caller must be allowlisted
        if (!passportType.isOpen) {
            if (!allowlist[tokenId][msg.sender]) revert NotAllowlisted();
        }

        _mint(msg.sender, tokenId, 1, "");

        emit PassportMinted(msg.sender, tokenId);
    }

    /**
     * @notice Mints a passport to a specific address (admin only)
     * @param to Address to mint passport to
     * @param tokenId The passport type to mint
     */
    function mintTo(address to, uint256 tokenId) external onlyOwner nonReentrant {
        if (!passportTypes[tokenId].exists) revert PassportTypeDoesNotExist();
        if (balanceOf(to, tokenId) > 0) revert AlreadyHoldsPassport();

        _mint(to, tokenId, 1, "");

        emit PassportMinted(to, tokenId);
    }

    // ============ View Functions ============

    /**
     * @notice Returns the URI for a specific passport type
     * @param tokenId The passport type ID
     */
    function uri(uint256 tokenId) public view override(ERC1155, IAssemblyPassports) returns (string memory) {
        if (!passportTypes[tokenId].exists) revert PassportTypeDoesNotExist();
        return passportTypes[tokenId].uri;
    }

    /**
     * @notice Returns the balance of passports held by an account for a specific type
     * @param account Address to query
     * @param tokenId Passport type ID
     */
    function balanceOf(
        address account,
        uint256 tokenId
    ) public view override(ERC1155, IAssemblyPassports) returns (uint256) {
        return super.balanceOf(account, tokenId);
    }

    /**
     * @notice Checks if an address holds a specific passport
     * @param account Address to check
     * @param tokenId Passport type ID
     */
    function holdsPassport(address account, uint256 tokenId) external view returns (bool) {
        return balanceOf(account, tokenId) > 0;
    }

    /**
     * @notice Checks if an address holds any of the specified passports
     * @param account Address to check
     * @param tokenIds Array of passport type IDs
     */
    function holdsAnyPassport(address account, uint256[] calldata tokenIds) external view returns (bool) {
        for (uint256 i = 0; i < tokenIds.length; i++) {
            if (balanceOf(account, tokenIds[i]) > 0) {
                return true;
            }
        }
        return false;
    }

    /**
     * @notice Gets the next token ID that will be assigned
     */
    function nextTokenId() external view returns (uint256) {
        return _nextTokenId;
    }

    // ============ Transfer Restrictions ============

    /**
     * @notice Overrides to make passports non-transferable (soulbound)
     */
    function safeTransferFrom(address, address, uint256, uint256, bytes memory) public pure override {
        revert TransfersNotAllowed();
    }

    /**
     * @notice Overrides to make passports non-transferable (soulbound)
     */
    function safeBatchTransferFrom(
        address,
        address,
        uint256[] memory,
        uint256[] memory,
        bytes memory
    ) public pure override {
        revert TransfersNotAllowed();
    }
}

contracts/Contest.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import { IContest } from "./interfaces/IContest.sol";
import { AssemblyPassports } from "./AssemblyPassports.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title Contest
 * @notice A time-bound voting mechanism for Assemblies to make decisions
 * @dev Designed to be cloned - uses initialize pattern instead of constructor
 */
contract Contest is IContest, ReentrancyGuard {
    // ============ State Variables ============

    /// @notice Maximum number of options allowed per contest
    uint256 public constant MAX_OPTIONS = 10;

    /// @notice Whether this contract has been initialized
    bool private _initialized;

    /// @notice The Assembly that created this contest
    address public assemblyAddress;

    /// @notice The passport contract for gating
    address public passports;

    /// @notice Contest prompt/question
    string public prompt;

    /// @notice Array of voting options
    string[] public options;

    /// @notice Timestamp when voting ends
    uint256 public votingEnd;

    /// @notice Required passport token IDs (empty array = open voting)
    uint256[] public requiredPassports;

    /// @notice Vote tallies: optionIndex => vote count
    mapping(uint256 => uint256) public voteTally;

    /// @notice Track who has voted: address => hasVoted
    mapping(address => bool) public hasVoted;

    /// @notice Track individual votes: address => optionIndex
    mapping(address => uint256) public userVote;

    /// @notice Total number of votes cast
    uint256 public totalVotes;

    // ============ Modifiers ============

    modifier onlyAssembly() {
        if (msg.sender != assemblyAddress) revert OnlyAssembly();
        _;
    }

    modifier votingActive() {
        if (block.timestamp >= votingEnd) revert VotingEnded();
        _;
    }

    modifier votingComplete() {
        if (block.timestamp < votingEnd) revert VotingNotEnded();
        _;
    }

    // ============ Initialization ============

    /**
     * @notice Initializes the contest (called by Assembly after cloning)
     * @param _assembly Address of the Assembly creating this contest
     * @param _passports Address of the passport contract
     * @param _prompt The question or prompt for this contest
     * @param _options Array of voting options
     * @param _votingDuration Duration of voting period in seconds
     * @param _requiredPassports Array of passport token IDs required to vote
     */
    function __init(
        address _assembly,
        address _passports,
        string memory _prompt,
        string[] memory _options,
        uint256 _votingDuration,
        uint256[] memory _requiredPassports
    ) external {
        if (_initialized) revert AlreadyInitialized();

        if (bytes(_prompt).length == 0) revert EmptyPrompt();
        if (_options.length < 2) revert NotEnoughOptions();
        if (_options.length > MAX_OPTIONS) revert TooManyOptions();
        if (_votingDuration == 0) revert InvalidVotingPeriod();

        // Validate all options are non-empty
        for (uint256 i = 0; i < _options.length; i++) {
            if (bytes(_options[i]).length == 0) revert EmptyOption();
        }

        assemblyAddress = _assembly;
        passports = _passports;
        prompt = _prompt;
        options = _options;
        votingEnd = block.timestamp + _votingDuration;
        requiredPassports = _requiredPassports;
        _initialized = true;

        emit ContestCreated(_assembly, _prompt, votingEnd, _options.length, _requiredPassports.length > 0);
    }

    // ============ Voting Functions ============

    /**
     * @notice Cast a vote for an option
     * @param optionIndex Index of the option to vote for
     */
    function vote(uint256 optionIndex) external nonReentrant votingActive {
        if (!_initialized) revert NotInitialized();
        if (hasVoted[msg.sender]) revert AlreadyVoted();
        if (optionIndex >= options.length) revert InvalidOption();

        // Check passport requirements if gated
        if (requiredPassports.length > 0) {
            if (!AssemblyPassports(passports).holdsAnyPassport(msg.sender, requiredPassports)) {
                revert MissingRequiredPassport();
            }
        }

        // Record the vote
        hasVoted[msg.sender] = true;
        userVote[msg.sender] = optionIndex;
        voteTally[optionIndex]++;
        totalVotes++;

        emit VoteCast(msg.sender, optionIndex);
    }

    // ============ View Functions ============

    /**
     * @notice Get all voting options
     */
    function getOptions() external view returns (string[] memory) {
        return options;
    }

    /**
     * @notice Get all required passport IDs
     */
    function getRequiredPassports() external view returns (uint256[] memory) {
        return requiredPassports;
    }

    /**
     * @notice Get complete vote results
     * @return optionNames Array of option strings
     * @return votes Array of vote counts for each option
     * @return total Total votes cast
     */
    function getResults() external view returns (string[] memory optionNames, uint256[] memory votes, uint256 total) {
        optionNames = options;
        votes = new uint256[](options.length);

        for (uint256 i = 0; i < options.length; i++) {
            votes[i] = voteTally[i];
        }

        total = totalVotes;
    }

    /**
     * @notice Get the winning option index
     * @dev In case of tie, returns the lowest index option
     * @return winningIndex Index of the winning option
     * @return winningVotes Number of votes for the winner
     */
    function getWinner() external view votingComplete returns (uint256 winningIndex, uint256 winningVotes) {
        winningIndex = 0;
        winningVotes = voteTally[0];

        for (uint256 i = 1; i < options.length; i++) {
            if (voteTally[i] > winningVotes) {
                winningIndex = i;
                winningVotes = voteTally[i];
            }
        }
    }

    /**
     * @notice Check if voting is currently active
     */
    function isActive() external view returns (bool) {
        return _initialized && block.timestamp < votingEnd;
    }

    /**
     * @notice Check if a specific address can vote
     * @param voter Address to check
     */
    function canVote(address voter) external view returns (bool) {
        if (!_initialized) return false;

        // Check if voting is active
        if (block.timestamp >= votingEnd) return false;

        // Check if already voted
        if (hasVoted[voter]) return false;

        // Check passport requirements
        if (requiredPassports.length > 0) {
            return AssemblyPassports(passports).holdsAnyPassport(voter, requiredPassports);
        }

        return true;
    }

    /**
     * @notice Get time remaining in voting period
     * @return seconds remaining (0 if ended)
     */
    function timeRemaining() external view returns (uint256) {
        if (!_initialized || block.timestamp >= votingEnd) return 0;
        return votingEnd - block.timestamp;
    }

    /**
     * @notice Check if contest is gated by passports
     */
    function isGated() external view returns (bool) {
        return requiredPassports.length > 0;
    }

    /**
     * @notice Check if contest has been initialized
     */
    function initialized() external view returns (bool) {
        return _initialized;
    }
}

